mapper.update:
UPDATE file
    SET file_name = #{fileName}
    ,SET file_path = #{filePath}
    ,SET file_size = #{fileSize}
    ,SET type = #{type}
    ,SET parent_table = #{parentTable}
    ,SET parent_no = #{parentNo}
WHERE id = #{id}

mapper.select:
SELECT *
FROM file
WHERE parent_table = #{parentTable} AND parent_no = #{parentNo}
